import {
  Button,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Input,
} from "@chakra-ui/core";
import React, { useEffect, useState } from "react";
import { Form, Formik } from "formik";

import Toast from "./../../components/Toast";
import HomeLayout from "../../components/HomeLayout";
import { FormInput } from "../../components/Form/FormInput";

// import logo from "./../assets/image/logo.png";

interface RegisterProps {}

const Register: React.FC<RegisterProps> = ({}) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    document.title = "Login";
  }, []);

  async function handleSubmit(event) {
    event.preventDefault();
    //Verificar se todos o campos estão preenchidos antes da primeira requisição.
    if (!username || !password) {
      Toast("Que vacilo! Você não preencheu todos os campos.", "warning");
      return false;
    }

    //Bloquear o botão de login para evitar requisições desnecessárias.
    let btn_login = document.getElementById("btn_login");
    btn_login.setAttribute("disabled", "disabled");
    btn_login.classList.add("waiting");

    //Tentar realizar a requisição.
    try {
      const response = await api.post("/users/login", { username, password });

      const { data } = response;

      console.log(data);
      data.login_date = new Date();

      dispatch(UserActions.login(JSON.stringify(data)));

      //TODO, método de redirecionamento vannila não fica legal :(
      window.location = "/Dashboard";
    } catch (error) {
      Toast(
        "Vacilou na senha ou usuario, verifique se suas credenciais estão corretas..",
        "warning"
      );

      btn_login.classList.remove("waiting");
      btn_login.removeAttribute("disabled");
    }
  }

  return (
    <HomeLayout>
      <Formik
        initialValues={{ username: "", password: "", email: "" }}
        onSubmit={(values) => {
          console.log(values);
        }}
      >
        {(values, handleChange) => (
          <Form>
            <FormControl>
              <FormInput name="email" placeholder="E-mail" label="E-mail" />
              <FormInput
                name="username"
                placeholder="Nome de Usuário"
                label="Nome de Usuário"
              />
              <FormInput
                name="password"
                placeholder="Senha"
                label="Senha"
                type="password"
              />
            </FormControl>

            <Button type="submit"></Button>
          </Form>
        )}
      </Formik>
    </HomeLayout>
  );
};

export default Register;
