import { Button, FormControl } from "@chakra-ui/core";
import { Form, Formik } from "formik";

import Toast from "./../../components/Toast";
import HomeLayout from "../../components/HomeLayout";
import { FormInput } from "../../components/Form/FormInput";
import { useMutation } from "urql";

// import logo from "./../assets/image/logo.png";

interface RegisterProps {}

const REGISTER_MUTATION = `mutation Register(
    options: { $password: String!, $username: String! }
    $email: String,
    $name: String) {
  register(
    options: { password: $password, username: $username }
    email: $email,
    name: $name
  ) {
    errors {
      field
      message
    }
    user {
      id
      username
      email
      updatedAt
      createdAt
    }
  }
}
`;

const Register: React.FC<RegisterProps> = ({}) => {
  const [res, register] = useMutation(REGISTER_MUTATION);

  function handleSubmit(values) {
    register({
      options: { username: values.username, password: values.password },
      email: values.email,
    });
  }

  return (
    <HomeLayout>
      <Formik
        initialValues={{ username: "", password: "", email: "", name: "" }}
        onSubmit={(values) => {
          console.log({
            options: { username: values.username, password: values.password },
            email: values.email,
            name: values.name,
          });
          register({
            options: { username: values.username, password: values.password },
            email: values.email,
            name: values.name,
          });
          console.log("what");
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <FormControl>
              <FormInput name="Nome" placeholder="Nome" label="Nome" />
              <FormInput
                name="email"
                placeholder="E-mail"
                label="E-mail"
                type="email"
              />
              <FormInput
                name="username"
                placeholder="Nome de Usuário"
                label="Nome de Usuário"
              />
              <FormInput
                name="password"
                placeholder="Senha"
                label="Senha"
                type="password"
              />
            </FormControl>

            <Button type="submit" isLoading={isSubmitting}>
              Cadastrar
            </Button>
          </Form>
        )}
      </Formik>
    </HomeLayout>
  );
};

export default Register;
